INSERT INTO `SG_categories` VALUES ('65', '94', '22', '17', '18', '5', 'sample-data-articles/joomla/extensions/modules/user-modules', 'com_content', 'Módulos Usuario', 'user-modules', '', '<p>Los módulos de usuario interactuan con el sistema del usuario, permitiendo a los usuarios acceder, mostrar quien está conectado, y mostrar la mayoría de los usuarios registrados recientemente.</p>', '1', '0', '0000-00-00 00:00:00', '1', '{\"target\":\"\",\"image\":\"\"}', '', '', '{\"page_title\":\"\",\"author\":\"\",\"robots\":\"\"}', '42', '2010-07-10 18:33:19', '0', '0000-00-00 00:00:00', '0', '*');
INSERT INTO `SG_categories` VALUES ('66', '95', '22', '19', '20', '5', 'sample-data-articles/joomla/extensions/modules/display-modules', 'com_content', 'Mostrar Módulos', 'display-modules', '', '<p>Estos módulos muestran información de otros componentes de contenido y usuario. Estos incluyen vínculos, fuentes de noticias y el gestor multimedia.</p>', '1', '0', '0000-00-00 00:00:00', '1', '{\"target\":\"\",\"image\":\"\"}', '', '', '{\"page_title\":\"\",\"author\":\"\",\"robots\":\"\"}', '42', '2010-07-10 18:34:49', '0', '0000-00-00 00:00:00', '0', '*');
