INSERT INTO `SG_content` VALUES ('56', '154', 'Usuarios en Linea', 'usuarios-en-linea', '', '<p>The Who\'s Online Module displays the number of Anonymous Users (e.g. Guests) and Registered Users (ones logged in) that are currently accessing the Web site. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Who.27s_Online\" title=\"Who\'s Online\">Help</a></p><p>{loadposition whosonlineload}</p>', '', '1', '0', '0', '65', '2010-07-10 23:06:16', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 23:06:16', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '1', '', '', '1', '3', '', '0', '*', '');
