INSERT INTO `SG_content` VALUES ('58', '156', 'Sandia maravillosa', 'sandia-maravillosa', '', '<p>La sandía es un regalo maravilloso y saludable. Producimos la sandía más dulce del mundo. Tenemos la sandía más grande producida en nuestro país.</p>', '', '1', '0', '0', '30', '2010-07-10 23:09:14', '42', 'Fruit Shop Webmaster', '2010-08-23 03:02:28', '42', '0', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '1', '', '', '1', '0', '', '0', '*', '');
