INSERT INTO `x0uw8_content` VALUES ('27', '125', 'Modulo Ultimos Articulos', 'latest-articles-module', '', '<p>This Module shows a list of the most recently published and current Articles. Some that are shown may have expired even though they are the most recent. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Latest_News\" title=\"Latest Articles\">Help</a></p>\r\n<p>{loadposition articleslatestload}</p>', '', '1', '0', '0', '64', '2010-07-10 21:08:42', '42', '', '2010-09-14 11:21:33', '42', '0', '0000-00-00 00:00:00', '2010-09-14 11:21:23', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '4', '0', '1', 'modules, content', '', '1', '5', '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('28', '126', 'Modulo Acceso', 'login', '', '<p>This module displays a username and password login form. It also displays a link to retrieve a forgotten password. If user registration is enabled (in the Global Configuration settings), another link will be shown to enable self-registration for users. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Login\" title=\"Login\">Help</a></p>\r\n<p>{loadposition loginload}</p>', '', '1', '0', '0', '78', '2010-07-10 21:10:16', '42', 'Joomla!', '2013-01-17 15:21:26', '42', '0', '0000-00-00 00:00:00', '2010-07-10 21:10:16', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', '9', '0', '5', '', '', '1', '6', '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('29', '127', 'Modulo Menu', 'menu-module', '', '<p>This module displays a menu on the site (frontend). Ã‚Â Menus can be displayed in a wide variety of ways by using the menu options and css menu styles.</p>\r\n<p>{loadposition menuload}</p>', '', '1', '0', '0', '75', '2010-07-10 21:21:52', '42', 'Joomla!', '2010-08-23 03:02:54', '42', '0', '0000-00-00 00:00:00', '2010-07-11 14:59:41', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '1', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('30', '128', 'Contenido mas leido', 'most-read-content', '', '<p>This Module shows a list of the currently published Articles which have the highest number of page views. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Most_Read_Content\" title=\"Most Read Content\">Help</a></p>\r\n<p>{loadposition articlespopularload}</p>', '', '1', '0', '0', '64', '2010-07-10 21:25:23', '42', 'Joomla!', '2010-09-14 11:21:05', '42', '0', '0000-00-00 00:00:00', '2010-07-10 21:25:23', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '5', '0', '2', 'modules, content', '', '1', '1', '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('32', '130', 'Opciones', 'opciones', '', '<p>As you make your Joomla! site using the extension you will control the details of the display using <em>parameter</em><strong>s</strong>. Parameters control everything from whether the author\'s name is displayed to who can view what to the number of items shown on a list.</p><p>Default parameters for each components are changed using the Options button on the component toolbar.</p><p>Parameters can also be set on an individual item, such as an article or contact and in menu links.</p><p>If you are happy with how your site looks, it is fine to leave all of the parameters set to the defaults that were created when your site was installed. As you become more experienced with Joomla! you will use parameters more.</p><p>Ã‚Â </p>', '', '1', '0', '0', '19', '2010-07-10 21:35:48', '42', 'Joomla!', '2010-08-23 03:02:28', '42', '0', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '10', '', '', '1', '11', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('36', '134', 'Modulo Imagen Aleatoria', 'modulo-imagen-aleatoria', '', '<p>This Module displays a random image from your chosen image directory. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Random_Image\" title=\"Random Image Module\">Help</a></p><p>{loadposition randomimageload}</p>', '', '1', '0', '0', '66', '2010-07-10 21:51:54', '42', '', '0000-00-00 00:00:00', '0', '0', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '4', '', '', '1', '2', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('37', '135', 'Modulo articulos relacionados', 'modulo-articulos-relacionados', '', '<p>This Module displays other Articles that are related to the one currently being viewed. These relations are established by the Meta Keywords.Ã‚Â Ã‚Â All the keywords of the current Article are searched against all the keywords of all other published articles. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Related_Articles\" title=\"Related Items Module\">Help</a></p>\r\n<p>{loadposition relateditemsload}</p>', '', '1', '0', '0', '64', '2010-07-10 21:53:14', '42', 'Joomla!', '2010-09-14 11:23:07', '42', '0', '0000-00-00 00:00:00', '2010-09-14 11:23:07', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '4', 'modules, content', '', '1', '4', '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('40', '138', 'Modulo de busqueda', 'modulo-busqueda', '', '<p>En este módulo mostrará un cuadro de búsqueda. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Search\" title=\"Search\">Help</a></p><p>{loadposition searchload}</p>', '', '1', '0', '0', '67', '2010-07-10 22:00:58', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:00:58', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '4', '', '', '1', '2', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('42', '140', 'Mapa del Sitio', 'mapa-sitio', '', '<p>{loadposition sitemapload}</p><p><em>By putting all of your content into nested categories you can give users and search engines access to everything using a menu.</em></p>', '', '1', '0', '0', '14', '2010-07-10 22:03:35', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:03:35', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '1', '', '', '1', '4', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('44', '142', 'Modulo Estadísticas', 'estadisticas', '', '<p>The Statistics Module shows information about your server installation together with statistics on the Web site users, number of Articles in your database and the number of Web links you provide.</p><p>{loadposition statisticsload}</p>', '', '1', '0', '0', '67', '2010-07-10 22:05:45', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:06:09', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '5', '', '', '1', '2', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('45', '143', 'Modulo sindicación', 'modulo-sindicacion', '', '<p>The syndicate module allows you to display a link that allows users to take a feed from your site. It will only display on pages for which feeds are possible. That means it will not display on single article, contact or weblinks pages, such as this one. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Syndicate\" title=\"Synicate Module\">Help</a></p><p>{loadposition syndicateload}</p>', '', '1', '0', '0', '67', '2010-07-10 22:07:51', '42', '', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:07:51', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '6', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('46', '144', 'Sistema', 'system', '', '<p>System plugins operate every time a page on your site loads. They control such things as your URLS, whether users can check a \"remember me\" box on the login module, and whether caching is enabled. Ã‚Â New in 1.6 is the redirect plugin that with the redirect component assist you in managing changes in URLs.</p><p>Default on:</p><ul><li>Remember me Help</li><li>SEFÃ‚Â Help</li><li>DebugÃ‚Â Help</li></ul><p>Default off:</p><ul><li>CacheÃ‚Â Help</li><li>LogÃ‚Â Help</li><li>RedirectÃ‚Â Help</li></ul>', '', '1', '0', '0', '25', '2010-07-10 22:09:41', '42', 'Joomla!', '2010-07-10 23:15:54', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:09:41', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '2', '', '', '1', '2', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('49', '147', 'Tipografia', 'tipografia', '', '<h1>H1 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h1><h2>H2 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h2><h3>H3 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h3><h4>H4 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h4><h5>H5 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h5><h6>H6 ABCDEFGHIJKLMNOPQRSTUVWXYZ abcdefghijklmonpqrstuvwzyz</h6><p>P The quick brown fox ran over the lazy dog. THE QUICK BROWN FOX RAN OVER THE LAZY DOG.</p><ul><li>Item</li><li>Item</li><li>Item<br /> <ul><li>Item</li><li>Item</li><li>Item<br /> <ul><li>Item</li><li>Item</li><li>Item</li></ul></li></ul></li></ul><ol><li>tem</li><li>Item</li><li>Item<br /> <ol><li>Item</li><li>Item</li><li>Item<br /><ol><li>Item</li><li>Item</li><li>Item</li></ol></li></ol> </li></ol>', '', '1', '0', '0', '23', '2010-07-10 22:22:26', '42', 'Joomla!', '2010-08-23 02:59:04', '42', '0', '0000-00-00 00:00:00', '2010-07-10 22:22:26', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '1', '', '', '1', '14', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('51', '149', 'Usuario', 'plugins-usuario', '', '<p>Por defecto habilitado:</p><ul><li>Ayuda Joomla!</li></ul><p>Por defecto deshabilitado:</p><p>Dos nuevos plugins están disponibles en 1.6, pero están desactivados por defecto.</p><ul><li>Crear Contacto, Ayuda<br />Crea un nuevo registro de contacto vinculado a cada nuevo usuario creado. </li><li>Perfil, Ayuda<br />Este plugin de perfil ejemplo permite insertar campos adicionales en el registro del usuario y mostrarlos el perfil. Esto pretende ser una muestra de los tipos de extensiones según el perfil que puede que desee crear.</li></ul>', '', '1', '0', '0', '25', '2010-07-10 22:43:34', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 23:16:15', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '4', '', '', '1', '2', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('52', '150', 'Usuarios', 'componente-usuarios', '', '<p>The users extension lets your site visitors register, login and logout, change their passwords and other information, and recover lost passwords. In the administrator it allows you to create, block and manage users and create user groups and access levels. Help</p>\r\n<p>Please note that some of the user views will not display if you are not logged in to the site.</p>', '', '1', '0', '0', '21', '2010-07-10 22:46:05', '42', 'Joomla!', '2010-08-23 03:00:39', '42', '42', '2013-01-17 15:23:38', '2010-07-10 23:13:53', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '4', '0', '5', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('54', '152', 'Enlaces Web', 'enlaces-web', '', '<p>Weblinks (com_weblinks) is a component that provides a structured way to organize external links and present them in a visually attractive, consistent and informative way. Help</p>', '', '1', '0', '0', '21', '2010-07-10 23:02:42', '42', 'Joomla!', '2010-08-23 03:00:39', '42', '0', '0000-00-00 00:00:00', '2010-07-10 23:02:42', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '3', '0', '6', '', '', '1', '10', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('55', '153', 'Modulo Enlaces Web', 'weblinks-module', '', '<p>This module displays the list of weblinks in a category. Ã‚Â <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Weblinks\" title=\"Weblinks Module\">Help</a></p><p>{loadposition weblinksload}</p>', '', '1', '0', '0', '66', '2010-07-10 23:04:41', '42', 'Joomla!', '2010-08-23 03:00:39', '42', '0', '0000-00-00 00:00:00', '2010-07-10 23:04:41', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '5', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('56', '154', 'Usuarios en Linea', 'usuarios-en-linea', '', '<p>The Who\'s Online Module displays the number of Anonymous Users (e.g. Guests) and Registered Users (ones logged in) that are currently accessing the Web site. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Who.27s_Online\" title=\"Who\'s Online\">Help</a></p><p>{loadposition whosonlineload}</p>', '', '1', '0', '0', '65', '2010-07-10 23:06:16', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-10 23:06:16', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '1', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('59', '157', 'Modulo Wrapper', 'wrapper-module', '', '<p>This module shows an iFrame window to specified location. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Wrapper\" title=\"Wrapper Module\">Help</a></p>\r\n<p>{loadposition wrapperload}</p>', '', '1', '0', '0', '9', '2010-07-10 23:10:10', '42', 'Joomla!', '2013-01-13 06:35:10', '42', '0', '0000-00-00 00:00:00', '2013-01-13 06:35:10', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"link_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_item_navigation\":\"\",\"show_icons\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_vote\":\"\",\"show_hits\":\"\",\"show_noauth\":\"\",\"urls_position\":\"\",\"alternative_readmore\":\"\",\"article_layout\":\"\",\"show_publishing_options\":\"\",\"show_article_options\":\"\",\"show_urls_images_backend\":\"\",\"show_urls_images_frontend\":\"\"}', '3', '0', '1', '', '', '1', '1', '{\"robots\":\"\",\"author\":\"\",\"rights\":\"\",\"xreference\":\"\"}', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('60', '158', 'Noticias Externas', 'news-feeds', '', '<p>News Feeds (com_newsfeeds) provides a way to organize and present news feeds. News feeds are a way that you present information from another site on your site. For example, the joomla.org website has numerous feeds that you an incorporate on your site. You an use menus to present a single feed, a list of feeds in a category, or or a list of all feed categories. Help</p>', '', '1', '0', '0', '21', '2010-07-11 00:28:23', '42', 'Joomla!', '2010-08-23 03:00:39', '42', '0', '0000-00-00 00:00:00', '2010-07-11 00:28:23', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '4', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('61', '159', 'Modulo Ruta', 'breadcrumbs-module', '', '<p>Breadcrumbs provide a pathway for users to navigate through the site. <a href=\"http://docs.joomla.org/Help15:Screen.modulessite.edit.15#Breadcrumbs\" title=\"Breacrumbs Module\">Help</a></p><p>{loadposition breadcrumbsload}</p>', '', '1', '0', '0', '67', '2010-07-11 01:30:25', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-11 01:30:25', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '2', '', '', '1', '3', '', '0', '*', '');
INSERT INTO `x0uw8_content` VALUES ('62', '160', 'Contenido', 'content-plugins', '', '<p>Content plugins run when specific kinds of pages are loaded. They do things ranging from protecting email addresses from harvesters to creating page breaks.</p><p>Default on:</p><ul><li>Email Cloaking Help</li><li>Load Module Help</li><li>Page Break Help</li><li>Page Navigation Help</li><li>Rating Help</li></ul><p>Default off:</p><ul><li>Code Highlighting (Geshi) Help</li></ul>', '', '1', '0', '0', '25', '2010-07-11 06:19:31', '42', 'Joomla!', '2010-08-23 02:58:49', '42', '0', '0000-00-00 00:00:00', '2010-07-11 06:19:31', '0000-00-00 00:00:00', '', '', '{\"show_title\":\"\",\"link_titles\":\"\",\"show_intro\":\"\",\"show_category\":\"\",\"link_category\":\"\",\"show_parent_category\":\"\",\"link_parent_category\":\"\",\"show_author\":\"\",\"show_create_date\":\"\",\"show_modify_date\":\"\",\"show_publish_date\":\"\",\"show_readmore\":\"\",\"show_print_icon\":\"\",\"show_email_icon\":\"\",\"show_hits\":\"\",\"page_title\":\"\",\"alternative_readmore\":\"\",\"layout\":\"\"}', '2', '0', '7', '', '', '1', '3', '', '0', '*', '');
